<body style="background:#282C34; color: white">

  <input type="hidden" name="setlogin" class="setlogin" value="true">

  <input type="email" name="iMail" class="iMail" value="">
  <input type="password" name="iPass" class="iPass" value="">
  <br><br>
  <button type="button" name="logIn" class="logIn" onclick="logIn()">LogIn</button>
  <button type="button" name="logOut" class="logOut" onclick="logOut()">LogOut</button>
  <br><br>
  <p class="uId">User Id: {uId}</p>
  <p class="uName">User Name: {uName}</p>
  <p class="uMail">User Test: {uMail}</p>
  <p class="uPoints">User Points: {uPoints}</p>
  <p class="uTotal">User Total Points: {uTotal}</p>
  <p class="uLoggedIn">false</p>

